import React from 'react';
import Logo from './components/logo/logo';
import Searchbar from './components/searchbar/searchbar';
import './App.css';
import Face from './components/face/face'
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import { getElementError } from '@testing-library/react';
const app = new Clarifai.App({
  apiKey: 'dbe7f57e47d64221ba1e86f2c712aef0'
 });
class App extends React.Component{
  constructor(){
    super();
    this.state={
    input:'',
    imageurl:'',
    box:''
    }
  }
  boxdetails=(data)=>{
   const details=data.outputs[0].data.regions[0].region_info.bounding_box;
   const pic=document.getElementById('image')
   const width=Number(pic.width);const height=Number(pic.height);
   return{
     leftcol:details.left_col*width,
     rightcol:width-details.rightcol*width,
     toprow:details.top_row*height,
     bottomrow:height-details.bottom_row*height
   }
  }
  inpchange=(event)=>{
    this.setState({input:event.target.value});
    }
  onbtnclk=()=>{
this.setState({imageurl:this.state.input});
console.log(this.state.input);
app.models.predict(Clarifai.FACE_DETECT_MODEL,this.state.input)
.then((response)=>this.boxdetails(response));}
  render(){
  return (
    <div>
      <Particles className='particles'
              params={{
            		particles: {
                  number:{
                    value:200,
                  }
            		}
            	}}
            />
      <Logo/>
      <div style={{display:'grid',gridTemplateColumns:'1fr 1fr 1fr'}}>
        <div style={{gridColumnStart:'2',gridColumnEnd:'3'}}>
    <div style={{display:'flex',flexDirection:'column'}}>
      <div style={{paddingLeft:'10px'}}>
    <p style={{fontStyle:'oblique',fontSize:'1.5em',marginBottom:'5px'}} className='grow'>FACE DETECT APP :)</p>
    </div>
    <div style={{paddingRight:'30%'}}>
    <div style={{border:'1px solid black',marginBottom:'5px'}}></div>
    </div>
    <div className='pa4 dib shadow-4 br3 custom'>
      <Searchbar inpchange={this.inpchange} onbtnclk={this.onbtnclk} className='grow'/>
     </div>
      <Face url={this.state.imageurl}/>
    </div>
    </div>
    </div>
    </div>
  );
}
}

export default App;
